import { Component, OnInit, Injectable } from '@angular/core';
import { ConfigService } from './config/config.service';
import { Block } from './model/block';

@Injectable()
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  constructor(private config: ConfigService) {}
  menu = { home: 'Home', about: 'About', gallery: 'Gallery' };
  block: any = {
    total_block_produced_within_time: 1,
    total_missed_blocks: 1,
    duration_in_hrs: 1,
    number_of_validators: 1,
  };

  async ngOnInit(): Promise<void> {
    const response = (await this.config.getMissedBlock()).subscribe((data) => {
      console.log(data);
      this.block = data;
    });
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="UTF-8" />
    <meta http-equiv="X-UA-Compatible" content="IE=edge" />
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <link
      href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css"
      rel="stylesheet"
      integrity="sha384-GLhlTQ8iRABdZLl6O3oVMWSktQOp6b7In1Zl3/Jr59b6EGGoI1aFkw7cmDA6j6gD"
      crossorigin="anonymous"
    />

    <title>Document</title>
  </head>
  <body>
    <app-header [menu]="menu"></app-header>
    <div class="container">
      <h1>{{ blocks.duration_in_hrs }}</h1>
      <app-friends></app-friends>
    </div>
    <script
      src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/js/bootstrap.bundle.min.js"
      integrity="sha384-w76AqPfDkMBDXo30jS1Sgez6pr3x5MlQ1ZAGC+nuZB+EYdgRZgiwxhTBTkF7CXvN"
      crossorigin="anonymous"
    ></script>
  </body>
</html>
